import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import Img from '../src/images/x-circle.svg'
import { Button, CardActionArea, CardActions, CardHeader, TextField } from '@mui/material';

export default function MultiActionAreaCard() {
  return (
    <Card sx={{ width:550 ,height:400}}>
      <CardActionArea>
        <CardHeader 
          sx={{backgroundColor:"#d3d3d3" , height:70 ,  fontSize:'small' , fontWeight: '1rem'}}

          title='Create an Organization'
         // avatar= "../src/images/x-circle.svg"
          avatar = {<Avatar src={Img} sx={{  display: 'flex',
          justifyItems: 'end'}}/> }
        />
        <CardContent>
          <Typography gutterBottom component="p" color="textSecondary" sx={{ mb:3, mt:2, fontSize:'small' }}>
            Organizations allow you to invite team members and assign them to workspaces
          </Typography>
          <div>
            <Typography sx={{ fontWeight: 'bolder' ,mb:1 , fontSize:'small'}} > Organization Name</Typography>
            <TextField id="outlined-basic" variant="outlined" fullWidth  size="small" />
          </div>
        </CardContent>
      </CardActionArea>
      <CardActions>
      <Button variant="contained" size="large" sx={{backgroundColor: "#9F77EB" ,fontSize:'small',fontWeight:'bold', textTransform: 'none', ml:1,
        ":hover": {
          bgcolor: "#9F77EB",
          color: "white",
        },}}>
        Create Organization
      </Button>
      </CardActions>
    </Card>
  );
}
